SET NAMES utf8mb4;
SET FOREIGN_KEY_CHECKS = 0;

-- ----------------------------
-- Table structure for t_blog
-- ----------------------------
DROP TABLE IF EXISTS `t_blog`;
CREATE TABLE `t_blog`  (
  `id` int(11) NOT NULL AUTO_INCREMENT,
  `title` varchar(200) CHARACTER SET utf8 COLLATE utf8_general_ci NULL DEFAULT NULL,
  `summary` varchar(400) CHARACTER SET utf8 COLLATE utf8_general_ci NULL DEFAULT NULL,
  `releaseDate` datetime(0) NULL DEFAULT NULL,
  `clickHit` int(11) NULL DEFAULT NULL,
  `replyHit` int(11) NULL DEFAULT NULL,
  `content` text CHARACTER SET utf8 COLLATE utf8_general_ci NULL,
  `typeId` int(11) NULL DEFAULT NULL,
  `keyWord` varchar(200) CHARACTER SET utf8 COLLATE utf8_general_ci NULL DEFAULT NULL,
  PRIMARY KEY (`id`) USING BTREE,
  INDEX `typeId`(`typeId`) USING BTREE,
  CONSTRAINT `t_blog_ibfk_1` FOREIGN KEY (`typeId`) REFERENCES `t_blogtype` (`id`) ON DELETE RESTRICT ON UPDATE RESTRICT
) ENGINE = InnoDB AUTO_INCREMENT = 12 CHARACTER SET = utf8 COLLATE = utf8_general_ci ROW_FORMAT = Dynamic;

-- ----------------------------
-- Records of t_blog
-- ----------------------------
INSERT INTO `t_blog` VALUES (1, '省委组织部、省委教育工委领导莅校调研“组团式”帮扶工作', '1月8日下午，省委组织部干部五处副调研员曾志学、省委教育工委干部处副处长林筱毅莅临我校调研指导“组团式”帮扶工作，在办公楼一号会议室召开调研座谈会。我校党委副书记黄达海，校长助理、华师帮扶队队长张正栋，党委常委、组织部部长邓逢光，华师帮扶队全体成员参加座谈。张正栋代表华师帮扶队汇报了一年围绕“一中心两支柱三对', '2020-01-13 08:45:44', 2, 0, '<p style=\"text-align: justify;line-height: 1.5;text-indent: 2em;font-family: 宋体;font-size: 14px;margin-top: 1em;margin-bottom: 1em;-ms-text-justify: inter-ideograph\">1月8日下午，省委组织部干部五处副调研员曾志学、省委教育工委干部处副处长林筱毅莅临我校调研指导“组团式”帮扶工作，在办公楼一号会议室召开调研座谈会。我校党委副书记黄达海，校长助理、华师帮扶队队长张正栋，党委常委、组织部部长邓逢光，华师帮扶队全体成员参加座谈。</p><p style=\"text-align: justify;line-height: 1.5;text-indent: 2em;font-family: 宋体;font-size: 14px;margin-top: 1em;margin-bottom: 1em;-ms-text-justify: inter-ideograph\">张正栋代表华师帮扶队汇报了一年围绕“一中心两支柱三对接四任务”帮扶工作思路开展帮扶工作情况、存在主要问题和下一步工作计划，其他帮扶队员分别畅谈了对口帮扶单位的帮扶措施与感受体会。</p><p style=\"text-align: justify;line-height: 1.5;text-indent: 2em;font-family: 宋体;font-size: 14px;margin-top: 1em;margin-bottom: 1em;-ms-text-justify: inter-ideograph\">黄达海代表学校党委对省委组织部、省委教育工委开展“组团式”帮扶工作助推学校“冲一流、补短板、强特色”表示感谢，对华师的全力支持和帮扶队员担当意识、无私奉献精神致以崇高敬意。他认为，省委组织部和省委教育工委开展的“组团式”帮扶工作是英明的决策。他指出，华师帮扶队的帮扶思路清晰，帮扶中心突出，帮扶成效显著，在短短一年时间内围绕学校的申硕做了大量卓有成效的工作。他希望，省委组织部、省委教育工委继续给予帮扶工作更多关心，华师对我校的帮扶工作需要进一步完善机制，开展精准帮扶，使帮扶工作实效更突显。</p><p style=\"text-align: justify;line-height: 1.5;text-indent: 2em;font-family: 宋体;font-size: 14px;margin-top: 1em;margin-bottom: 1em;-ms-text-justify: inter-ideograph\">林筱毅对华师帮扶队工作给予充分的肯定，认为帮扶队制定的帮扶工作思路有很强的指导性，短短一年的时间里为岭南师院申硕工作、学科建设、“新师范”建设、科研平台和科研项目等做了很多具体且有成效的工作。她强调，两校党委高度重视，提早科学谋划，立足实际，扎实推进帮扶工作，成效初显。华师帮扶队队员讲政治、顾大局、站位高，全身心投入，有奉献有担当。她希望两校党委和帮扶团队要继续按照上级有关文件精神和对口帮扶框架协议要求，创新帮扶方式，充分发挥帮扶团队的示范引领作用，面深入推进帮扶工作，高质量完成“组团式”帮扶工作任务。</p><p style=\"text-align: justify;line-height: 1.5;text-indent: 2em;font-family: 宋体;font-size: 14px;margin-top: 1em;margin-bottom: 1em;-ms-text-justify: inter-ideograph\">曾志学认为华师对岭师的“组团式”帮扶工作具有合作实、思路清、成效好、奉献多的特点，希望两校及时总结经验，建立长效机制，在帮扶任务结束时取得实实在在的成效。</p><p><br/></p>', 1, '组团式');
INSERT INTO `t_blog` VALUES (2, '开创思政课新范式 大学生当“宣讲员”', '学校党委宣传部、马克思主义学院为推进习近平新时代中国特色的社会主义思想深入人心、落地生根，率先尝试在概论课教学中开设大学生学习习近平新时代中国特色社会主义思想大讲堂，打造宣讲+师生共建学习团队模式，让大学生走上讲坛，用带有青春气息的特色宣讲，传播党的理论和政策。1月5日，首期学习大讲堂开讲，来自数学与统计学院', '2020-01-13 08:46:50', 0, 0, '<p style=\"TEXT-ALIGN: justify; LINE-HEIGHT: 1.5; MARGIN-TOP: 1em; TEXT-INDENT: 2em; FONT-FAMILY: 宋体; MARGIN-BOTTOM: 1em; FONT-SIZE: 10.5pt\">学校党委宣传部、马克思主义学院为推进习近平新时代中国特色的社会主义思想深入人心、落地生根，率先尝试在概论课教学中开设<span style=\"background-color: rgb(255, 255, 255);\">大学生学习习近平新时代中国特色社会主义思想大讲堂</span>，打造宣讲+师生共建学习团队模式，让大学生走上讲坛，用带有青春气息的特色宣讲，传播党的理论和政策。</p><p style=\"TEXT-ALIGN: justify; LINE-HEIGHT: 1.5; MARGIN-TOP: 1em; TEXT-INDENT: 2em; FONT-FAMILY: 宋体; MARGIN-BOTTOM: 1em; FONT-SIZE: 10.5pt\">1月5日，首期学习大讲堂开讲，来自数学与统计学院的赖政融、罗志明、付隆琛、钟庆烨、李恺东和美术与设计学院的史昊祯、李建谅、郭敬宇等8名同学作为首批宣讲员，就“建设美丽中国”“构建人类命运共同体”“不忘初心、牢记使命”“演绎红色经典，传承爱国热情”以及“新时代青年的使命担当”等主题进行了宣讲。</p><p style=\"TEXT-ALIGN: justify; LINE-HEIGHT: 1.5; MARGIN-TOP: 1em; TEXT-INDENT: 2em; FONT-FAMILY: 宋体; MARGIN-BOTTOM: 1em; FONT-SIZE: 10.5pt\">“参加学习大讲堂，对我来说是一次思想大洗礼、能力大提升。”赖政融说，这让自己对党的创新理论有了更深刻的认识，也坚定了自己向党组织靠拢的决心。付隆琛为了不断完善宣讲稿，主动查阅资料学习理论知识，觉得自己的思想更加充实了，对“心中有理想，脚下有力量”这名话的理解更加深入透切了。</p><p style=\"TEXT-ALIGN: justify; LINE-HEIGHT: 1.5; MARGIN-TOP: 1em; TEXT-INDENT: 2em; FONT-FAMILY: 宋体; MARGIN-BOTTOM: 1em; FONT-SIZE: 10.5pt\">马克思主义学院党总支书记谢应明介绍，概论课党支部是省委教育工委的“三型”党支部，也是省委教育工委、教育部党建“双创”工作样板培育创建单位，通过党建示范引领，实行与二级学院学生党支部签订共建协议，指导学生建立学习小组，构建学习平台和良性互动机制，着力打造堂上堂下、课内课外、师生之间、学生之间、小组之间“五位一体”的学习共同体，实现师生树信仰、建忠诚、讲奉献的目标。</p><p style=\"TEXT-ALIGN: justify; LINE-HEIGHT: 1.5; MARGIN-TOP: 1em; TEXT-INDENT: 2em; FONT-FAMILY: 宋体; MARGIN-BOTTOM: 1em; FONT-SIZE: 10.5pt\">为了使宣讲深入人心，宣讲员通过幻灯片、短视频等多种形式，让宣讲变得生动有趣，首期活动在学生中引起了强烈的反响。学生党员张颂轩认为，宣讲员结合自身专业、经历和特长来讲述自己对习近平新时代中国特色社会主义思想的理解让人印象深刻。许多同学由衷地表示，这样的思政课形式新颖。</p><p style=\"TEXT-ALIGN: justify; LINE-HEIGHT: 1.5; MARGIN-TOP: 1em; TEXT-INDENT: 2em; FONT-FAMILY: 宋体; MARGIN-BOTTOM: 1em; FONT-SIZE: 10.5pt\">马克思主义学院院长何增光教授认为，这是思政课教学的一次创新，实现了由教师讲思政课向学生讲思政课的转变。学生通过参与其中，带来的不仅是自我学习能力的突破，更是新思想新理念给其带来一生的受益。</p><p style=\"TEXT-ALIGN: justify; LINE-HEIGHT: 1.5; MARGIN-TOP: 1em; TEXT-INDENT: 2em; FONT-FAMILY: 宋体; MARGIN-BOTTOM: 1em; FONT-SIZE: 10.5pt\">马克思主义学院计划在两年内，依托大学生学习大讲堂，组建100个学习小组，教师和学生学习小组形成学前辅导、学中交流、学后宣讲的良性互动机制，每个学期开展4次宣讲，重点围绕“学习新思想”“社会主义好”“中国共产党为什么能、为什么行、为什么好”“强国有我”“守护祖国意识形态南大门”等板块展开宣讲。</p><p><br/></p>', 1, '“宣讲员”');
INSERT INTO `t_blog` VALUES (3, '[南方+]岭南师范学院探索思政课新范式', '原标题链接：思政课也玩“反转”！岭南师范学院探索思政课新范式近日，岭南师范学院举行首期大学生学习大讲堂，8名学生作为首批宣讲员，就“建设美丽中国”“构建人类命运共同体”“不忘初心、牢记使命”“演绎红色经典，传承爱国热情”以及“新时代青年的使命担当”等主题进行了宣讲。“反转”的思政课堂，富有青春气息的特色宣讲，', '2020-01-13 08:47:53', 0, 0, '<p style=\"text-align: justify; text-indent: 2em; font-family: 宋体; font-size: 10.5pt; margin-top: 1em; margin-bottom: 1em; text-justify: inter-ideograph;\">原标题链接：<a style=\"font-size: 10.5pt;\" href=\"https://static.nfapp.southcn.com/content/202001/08/c2968542.html?colID=null&appversion=6200&firstColID=3829&from=groupmessage\">思政课也玩“反转”！岭南师范学院探索思政课新范式</a></p><p style=\"text-align: justify; text-indent: 2em; font-family: 宋体; font-size: 10.5pt; margin-top: 1em; margin-bottom: 1em; text-justify: inter-ideograph;\"><br/></p><p style=\"text-align: justify; text-indent: 2em; font-family: 宋体; font-size: 10.5pt; margin-top: 1em; margin-bottom: 1em; text-justify: inter-ideograph;\">近日，岭南师范学院举行首期大学生学习大讲堂，8名学生作为首批宣讲员，就“建设美丽中国”“构建人类命运共同体”“不忘初心、牢记使命”“演绎红色经典，传承爱国热情”以及“新时代青年的使命担当”等主题进行了宣讲。“反转”的思政课堂，富有青春气息的特色宣讲，获得师生的好评。</p><p style=\"text-align: justify; text-indent: 2em; font-family: 宋体; font-size: 10.5pt; margin-top: 1em; margin-bottom: 1em; text-justify: inter-ideograph;\">近期，岭南师范学院党委宣传部、马克思主义学院尝试在概论课教学中开设大学生学习大讲堂，打造“宣讲+师生共建学习团队”模式，让大学生走上讲坛，传播党的理论和政策。</p><p style=\"text-align: justify; text-indent: 2em; font-family: 宋体; font-size: 10.5pt; margin-top: 1em; margin-bottom: 1em; text-justify: inter-ideograph;\">“参加学习大讲堂，对我来说是一次思想大洗礼、能力大提升。”数学与统计学院学生赖政融说，这让自己对党的创新理论有了更深刻的认识，也坚定了自己向党组织靠拢的决心。该院学生付隆琛为了不断完善宣讲稿，主动查阅资料学习理论知识，让自己的思想更加充实，对“心中有理想，脚下有力量”这句话的理解更加深入。</p><p style=\"text-align: justify; text-indent: 2em; font-family: 宋体; font-size: 10.5pt; margin-top: 1em; margin-bottom: 1em; text-justify: inter-ideograph;\">马克思主义学院党总支书记谢应明介绍，作为省委教育工委的“三型”党支部，省委教育工委、教育部党建“双创”工作样板培育创建单位，岭南师范学院概论课党支部通过党建示范引领，与二级学院学生党支部签订共建协议，指导学生建立学习小组，构建学习平台和良性互动机制，着力打造堂上堂下、课内课外、师生之间、学生之间、小组之间“五位一体”的学习共同体，实现师生树信仰、建忠诚、讲奉献的目标。</p><p style=\"text-align: justify; text-indent: 2em; font-family: 宋体; font-size: 10.5pt; margin-top: 1em; margin-bottom: 1em; text-justify: inter-ideograph;\">为了使宣讲深入人心，宣讲员通过幻灯片、短视频等多种资料，让宣讲变得生动有趣，在学生中引起了强烈的反响。学生党员张颂轩认为，宣讲员结合自身专业、经历和特长来讲述自己对党和国家大政方针的理解让人印象深刻，这样的思政课形式新颖，不再枯燥。</p><p style=\"text-align: justify; text-indent: 2em; font-family: 宋体; font-size: 10.5pt; margin-top: 1em; margin-bottom: 1em; text-justify: inter-ideograph;\">马克思主义学院院长何增光教授认为，这是思政课教学的一次创新，实现了由“教师讲思政课”向“学生讲思政课”的转变。学生参与其中，不仅有自我学习能力的突破，更有新思想新理念使其一生受益。据介绍，马克思主义学院计划在两年内，依托大学生学习大讲堂，在全校组建100个学习小组，教师和学生学习小组形成学前辅导、学中交流、学后宣讲的良性互动机制，每个学期开展4次宣讲。</p><p><br/></p>', 2, '新范式');
INSERT INTO `t_blog` VALUES (4, '[光明日报]岭南师范学院：聚焦检视整改推动学校内涵式高质量发展', '链接：岭南师范学院：聚焦检视整改推动学校内涵式高质量发展1月2日，记者在岭南师范学院了解到，学校自“不忘初心、牢记使命”主题教育开展以来，坚持把检视整改落到实处，拿出硬招实招服务师生，以主题教育的新成效，推动学校内涵式高质量发展。在主题教育开展过程中，岭南师院领导班子纳入《检视问题和整改落实清单》的问题36个', '2020-01-13 08:48:39', 0, 0, '<p style=\"margin-top: 1em; margin-bottom: 1em; text-indent: 2em; text-align: justify; line-height: 1.5; font-family: 宋体; font-size: 10.5pt;\" align=\"\">链接：<a href=\"http://share.cloud.gmw.cn/yunmei-share/?shareTitle=%E5%B2%AD%E5%8D%97%E5%B8%88%E8%8C%83%E5%AD%A6%E9%99%A2%EF%BC%9A%E8%81%9A%E7%84%A6%E6%A3%80%E8%A7%86%E6%95%B4%E6%94%B9%E6%8E%A8%E5%8A%A8%E5%AD%A6%E6%A0%A1%E5%86%85%E6%B6%B5%E5%BC%8F%E9%AB%98%E8%B4%A8%E9%87%8F%E5%8F%91%E5%B1%95&shareImg=https://s.cloud.gmw.cn/2016/json/upload/resources/image/2016/12/13/6100070.png&shareDes=&sourceUrl=http://s.cloud.gmw.cn/gmrb/c/2020-01-02/1339711.shtml&articleId=1339711&token=1c907ac6cbce390d9e75578307771d63&from=singlemessage\">岭南师范学院：聚焦检视整改推动学校内涵式高质量发展</a></p><p style=\"margin-top: 1em; margin-bottom: 1em; text-indent: 2em; text-align: justify; line-height: 1.5; font-family: 宋体; font-size: 10.5pt;\" align=\"\">1月2日，记者在岭南师范学院了解到，学校自“不忘初心、牢记使命”主题教育开展以来，坚持把检视整改落到实处，拿出硬招实招服务师生，以主题教育的新成效，推动学校内涵式高质量发展。</p><p style=\"margin-top: 1em;margin-bottom: 1em;text-indent: 2em;text-align: justify;line-height: 1.5;font-family: 宋体;font-size: 14px\" align=\"\">在主题教育开展过程中，岭南师院领导班子纳入《检视问题和整改落实清单》的问题36个，已经解决11个，正在整改25个。其中，学校党委把调研检视出来的“提升办学层次和改善办学条件”，作为学校发展的最迫切需要解决的问题，提出了申请硕士学位授予权、征地建新校区的两大举措，全力以赴破解学校发展瓶颈。</p><p style=\"margin-top: 1em;margin-bottom: 1em;text-indent: 2em;text-align: justify;line-height: 1.5;font-family: 宋体;font-size: 14px\" align=\"\">学校紧紧围绕申硕核心工作，2019年引进博士116人、双聘院士2人，建起院士工作站，获批广东省重点实验室，获省教学成果奖一等奖3项，二等奖4项，2个师范专业1个非师范专业通过专业认证，“孔子课堂”升格为孔子学院，科研立项共178项，其中国家级16项、省部级62项，国家和省部级项目立项数均创年度新高，主题教育与学校事业内涵式高质量发展相得益彰。</p><p style=\"margin-top: 1em;margin-bottom: 1em;text-indent: 2em;text-align: justify;line-height: 1.5;font-family: 宋体;font-size: 14px\" align=\"\">2019年12月20日，学校湖光校区第一根定位桩正式打下。同日，湖光校区建设项目（首期）可行性研究报告获得了湛江市发展改革局正式立项。湖光校区规划总建筑面积约58万平方米，总投资预计约28亿元。按照“整体规划、分期实施”原则，分期分批建设。首期建设项目总建筑面积243525平方米，投资18.94亿元。</p><p style=\"margin-top: 1em;margin-bottom: 1em;text-indent: 2em;text-align: justify;line-height: 1.5;font-family: 宋体;font-size: 14px\" align=\"\">1月2日，学校南大门揭牌开通和新增教师技能实训中心正式揭幕投入使用，岭南师院兰艳泽校长介绍，南大门及路段建设，既是校园基础设施建设工程，也是一项民生工程，有效解决长期困扰广大师生出门即堵车的问题。新增的实训中心，是学校坚持“两性两型”办学定位，推进“新师范”建设的重要举措。学校从主题教育一开始就扭住师生关注的民生工程，大力推进教职工宿舍楼安装电梯、新引进人才公寓和配偶工作安排待遇偏低、校园车辆出入管理较乱、学生宿舍暖心服务等问题不放，整改落实一竿到底立行立改。</p><p style=\"margin-top: 1em;margin-bottom: 1em;text-indent: 2em;text-align: justify;line-height: 1.5;font-family: 宋体;font-size: 14px\" align=\"\">“我们就是要通过各种有力的举措真正解决问题，让广大师生实实在在地感受到开展主题教育带来的新变化，有效增强了师生的获得感、幸福感和安全感，将主题教育的新成效转变为推动学校内涵式高质量发展的强大动力。”岭南师院党委书记刘明贵介绍说。</p><p><br/></p>', 2, '内涵式');
INSERT INTO `t_blog` VALUES (5, '关于开通车辆来校预约系统微信端使用功能的通知', '保卫〔2020〕1号校内各单位：为方便师生对校外来访车辆的预约申请，我处在车辆来校预约系统PC端使用功能的基础上研发了手机微信端预约功能，该功能现正式开通使用，使用说明详见附件，请各单位及时将本通知转达给师生。使用过程中如有疑问和建议请联系：石军民，联系电话3183777；刘天明，联系电话3182444。附件', '2020-01-13 08:49:23', 0, 0, '<form name=\"_newscontent_fromname\"><p class=\"vsbcontent_start\" style=\"text-align: center;\">保卫〔2020〕1号</p><p class=\"vsbcontent_start\"><span style=\"font-size: 21px;\">校内各单位：</span></p><p><span style=\"font-size: 21px;\">为方便师生对校外来访车辆的预约申请，我处在车辆来校预约系统PC端使用功能的基础上研发了手机微信端预约功能，该功能现正式开通使用，使用说明详见附件，请各单位及时将本通知转达给师生。</span></p><p><span style=\"font-size: 21px;\">使用过程中如有疑问和建议请联系：石军民，联系电话3183777；刘天明，联系电话3182444。</span></p><p><span style=\"font-size: 21px;\">附件：岭南师范学院车辆来校预约系统微信端使用说明</span></p><p style=\"text-align: right;\"><span style=\"font-size: 21px;\">保卫处</span></p><p class=\"vsbcontent_end\" style=\"text-align: right;\"><span style=\"font-size: 21px;\">2020年1月13日</span></p><p><br/></p><ul style=\"list-style-type: none;\" class=\" list-paddingleft-2\"><li><p>附件【<a href=\"http://www.lingnan.edu.cn/system/_content/download.jsp?urltype=news.DownloadAttachUrl&owner=1573552155&wbfileid=2286093\" target=\"_blank\">附件 岭南师范学院车辆来校预约系统微信端使用说明.docx</a>】已下载<span id=\"nattach2286093\">791</span>次</p></li></ul><p><br/></p></form><p><br/></p>', 3, '开通车辆');
INSERT INTO `t_blog` VALUES (6, '2019年教职工困难补助和互助基金补助名单公示', '2020-01-10 （点击数：247）校内各单位：    为做好2020年春节我校教职工困难补助工作，体现学校对特殊困难教职工的关心和爱护，校工会分别于2019年12月24日和 2019 年 12 月 26日发出了《关于做好2019年教职工特殊困难补助的通知》、《关于做好2019年教职工互助基金补助工作的通', '2020-01-13 08:50:23', 1, 0, '<h3 style=\"text-align: center\">2020-01-10 （点击数：<span id=\"dynclicks_wbnews_28162_713\" name=\"dynclicks_wbnews_28162_713\">247</span>）</h3><p><span style=\"font-family: 仿宋;font-size: 20px\">校内各单位：</span></p><p><span style=\"font-family: 仿宋;font-size: 20px\">&nbsp;&nbsp;&nbsp; 为做好2020年春节我校教职工困难补助工作，体现学校对特殊困难教职工的关心和爱护，校工会分别于2019年12月24日和 2019 年 12 月 26日发出了《关于做好2019年教职工特殊困难补助的通知》、《关于做好2019年教职工互助基金补助工作的通知》，在申报期限内，共有14名教职工提出申请困难补助，10名教职工申请互助基金补助。 2020年1月 10日上午，校工会召开教代会生活福利工作委员会扩大会议，对所有申报材料进行了认真的审核和研究，根据有关规定和实际情况，会议决定拟对许家家等8名教职工发放特殊困难补助，对张艳等9名教职工给予互助基金补助。</span></p><p><span style=\"font-family: 仿宋;font-size: 20px\">&nbsp;&nbsp;&nbsp; 现将名单予以公示，公示时间为 2020年 1 月 11 日至16日。如对公示补助人员有异议，请于公示期内，通过来信(署名)、来电、来访等形式向校工会反映（联系人：孙老师3183212 ，林老师3183170 ）。</span></p><p><span style=\"font-family: 仿宋;font-size: 20px\">&nbsp;&nbsp;&nbsp; 附件：2019年特殊困难补助和互助基金补助教职工名单</span></p><p>&nbsp;</p><p><span style=\"font-family: 仿宋;font-size: 20px\"> &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; </span><span style=\"font-family: 仿宋;font-size: 20px\">校工会</span></p><p><span style=\"font-family: 仿宋;font-size: 20px\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; &nbsp;2020</span><span style=\"font-family: 仿宋;font-size: 20px\">年1月10日 </span></p><p>&nbsp;</p><p><br/></p><ul style=\"list-style-type: none;\" class=\" list-paddingleft-2\"><li><p>附件【<a href=\"http://www.lingnan.edu.cn/system/_content/download.jsp?urltype=news.DownloadAttachUrl&owner=1573552155&wbfileid=2286208\" target=\"_blank\">2019年特殊困难补助教职工名单.docx</a>】已下载<span id=\"nattach2286208\">232</span>次</p></li></ul><p><br/></p>', 3, '基金补助名单公示');
INSERT INTO `t_blog` VALUES (7, '地球大龟裂——基于力学的地学思考---大连理工大学唐春安教授报告会(“雷阳大讲坛”之四百七十五)', '2019-12-23 （点击数：115） 题目：地球大龟裂——基于力学的地学思考   时间：2019年12月23日晚19:30时地点：图书馆学术报告厅   报告人简介：唐春安，1984年和1988年分别获得东北大学硕士和博士学位，1991-1992赴英国帝国理工学院做博士后研究。国家教育部长江学者特聘教授，现', '2020-01-13 08:51:08', 1, 0, '<h3 style=\"text-align: center\">2019-12-23 （点击数：<span id=\"dynclicks_wbnews_28007_519\" name=\"dynclicks_wbnews_28007_519\">115</span>）</h3><p style=\";text-align: center;line-height: 29px\"><strong><span style=\"font-family: 宋体;font-size: 16px\">&nbsp;</span></strong></p><p style=\";line-height: 29px\"><span style=\"font-family: 宋体;font-size: 16px\">题目</span><span style=\"font-family: 宋体;font-size: 16px\">：</span><span style=\"font-family: 宋体;font-size: 16px\">地球大龟裂——基于力学的地学思考</span><span style=\"font-family: 黑体;font-size: 16px\">&nbsp;</span><span style=\"font-family: 宋体;font-size: 16px\">&nbsp;</span><span style=\"font-family: 宋体;font-size: 16px\">&nbsp;</span></p><p style=\";line-height: 29px\"><span style=\"font-family: 宋体;font-size: 16px\">时间：</span><span style=\"font-family: 宋体;font-size: 16px\">2019</span><span style=\"font-family: 宋体;font-size: 16px\">年</span><span style=\"font-family: 宋体;font-size: 16px\">12</span><span style=\"font-family: 宋体;font-size: 16px\">月</span><span style=\"font-family: 宋体;font-size: 16px\">23</span><span style=\"font-family: 宋体;font-size: 16px\">日晚</span><span style=\"font-family: 宋体;font-size: 16px\">19:30</span><span style=\"font-family: 宋体;font-size: 16px\">时</span></p><p style=\";line-height: 29px\"><span style=\"font-family: 宋体;font-size: 16px\">地点：</span><span style=\"font-family: 宋体;font-size: 16px\">图书馆学术报告厅</span><span style=\"font-family: 黑体;font-size: 16px\">&nbsp;</span></p><p style=\"line-height: 140%;margin-left: 32px\"><span style=\"line-height: 140%;font-family: 宋体;font-size: 16px\">&nbsp;&nbsp;</span></p><p style=\"line-height: 140%\"><span style=\"line-height: 140%;font-family: 宋体;font-size: 16px\">报告人简介</span><span style=\"line-height: 140%;font-family: 宋体;font-size: 16px\">：</span></p><p style=\"line-height: 140%;text-indent: 32px\"><span style=\"line-height: 140%;font-family: 宋体;font-size: 16px\">唐春安，</span><span style=\"line-height: 140%;font-family: 宋体;font-size: 16px\">1984</span><span style=\"line-height: 140%;font-family: 宋体;font-size: 16px\">年和</span><span style=\"line-height: 140%;font-family: 宋体;font-size: 16px\">1988</span><span style=\"line-height: 140%;font-family: 宋体;font-size: 16px\">年分别获得东北大学硕士和博士学位，</span><span style=\"line-height: 140%;font-family: 宋体;font-size: 16px\">1991-1992</span><span style=\"line-height: 140%;font-family: 宋体;font-size: 16px\">赴英国帝国理工学院做博士后研究。国家教育部长江学者特聘教授，现为大连理工大学岩土工程学科教授、中国地质大学（武汉）大地构造学科首席教授（兼职）。国家重大基础研究计划</span><span style=\"line-height: 140%;font-family: 宋体;font-size: 16px\">973</span><span style=\"line-height: 140%;font-family: 宋体;font-size: 16px\">项目首席科学家。国务院第五届地矿油学科评议组成员；中国岩石力学与工程学会副理事长，曾任国际岩石力学学会中国国家小组主席；《岩石力学与工程学报》副主编、《</span><span style=\"line-height: 140%;font-family: 宋体;font-size: 16px\">International Journal of Rock Mechanics and Mining Sciences</span><span style=\"line-height: 140%;font-family: 宋体;font-size: 16px\">》、《</span><span style=\"line-height: 140%;font-family: 宋体;font-size: 16px\">GeoMechanics and GeoEngineering</span><span style=\"line-height: 140%;font-family: 宋体;font-size: 16px\">》杂志编委。</span><span style=\"line-height: 140%;font-family: 宋体;font-size: 16px\">&nbsp;</span><span style=\"line-height: 140%;font-family: 宋体;font-size: 16px\"><br/></span><span style=\"line-height: 140%;font-family: 宋体;font-size: 16px\">&nbsp;&nbsp;&nbsp;&nbsp;</span><span style=\"line-height: 140%;font-family: 宋体;font-size: 16px\">主要从事岩石破坏机理及其岩石工程灾害模拟分析、监测预警方面研究工作。发表各类学术论文</span><span style=\"line-height: 140%;font-family: 宋体;font-size: 16px\">400</span><span style=\"line-height: 140%;font-family: 宋体;font-size: 16px\">余篇（其中</span><span style=\"line-height: 140%;font-family: 宋体;font-size: 16px\">SCI</span><span style=\"line-height: 140%;font-family: 宋体;font-size: 16px\">收录</span><span style=\"line-height: 140%;font-family: 宋体;font-size: 16px\">120</span><span style=\"line-height: 140%;font-family: 宋体;font-size: 16px\">余篇）。出版《</span><span style=\"line-height: 140%;font-family: 宋体;font-size: 16px\">Rock Failure Mechanism</span><span style=\"line-height: 140%;font-family: 宋体;font-size: 16px\">》等学术专著六部。获得国家科技进步二等奖</span><span style=\"line-height: 140%;font-family: 宋体;font-size: 16px\">及</span><span style=\"line-height: 140%;font-family: 宋体;font-size: 16px\">省部级二等以上科技成果奖励七次。分别于</span><span style=\"line-height: 140%;font-family: 宋体;font-size: 16px\">1994</span><span style=\"line-height: 140%;font-family: 宋体;font-size: 16px\">年和</span><span style=\"line-height: 140%;font-family: 宋体;font-size: 16px\">1995</span><span style=\"line-height: 140%;font-family: 宋体;font-size: 16px\">年获得国家教委“跨世纪优秀人才培养计划”和“国家杰出青年科学基金”的资助。曾荣获第四届中国青年科技奖，连续四年入榜爱思唯尔（</span><span style=\"line-height: 140%;font-family: 宋体;font-size: 16px\">Elsevier</span><span style=\"line-height: 140%;font-family: 宋体;font-size: 16px\">）地球与行星科学学科中国高被引学者榜单。</span></p><p style=\"line-height: 140%\"><span style=\"line-height: 140%;font-family: 宋体;font-size: 16px\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;</span></p><p style=\"line-height: 140%;text-indent: 32px\"><span style=\"line-height: 140%;font-family: 宋体;font-size: 16px\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;</span></p><p style=\"line-height: 140%;text-indent: 336px\"><span style=\"line-height: 140%;font-family: 宋体;font-size: 16px\">&nbsp;&nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 主</span><span style=\"line-height: 140%;font-family: 宋体;font-size: 16px\">办：物理科学与技术学院</span></p><p style=\"line-height: 140%;text-indent: 32px\"><span style=\"line-height: 140%;font-family: 宋体;font-size: 16px\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;协办：</span><span style=\"line-height: 140%;font-family: 宋体;font-size: 16px\">科学研究处</span></p><p><br/></p>', 4, '地球大龟裂');
INSERT INTO `t_blog` VALUES (8, '[学习强国]湛江市市长姜建军为岭南师院师生上思政课', '原标题链接：湛江市市长姜建军为岭南师院师生上思政课 宣讲《粤港澳大湾区发展规划纲要》12月25日，湛江市委副书记、市长姜建军到岭南师范学院为该校师生上思政课，以《抓住大机遇\n \n融入大湾区——学习贯彻<粤港澳大湾区发展规划纲要>》为题开讲。思政课由岭南师范学院党委书记刘明贵主持。开课前，该校校长兰艳泽为姜建军', '2020-01-13 08:52:53', 6, 1, '<p style=\"margin-top: 1em; margin-bottom: 1em; text-indent: 2em; text-align: justify; font-size: 10.5pt;\" align=\"\">原标题链接：<a href=\"https://article.xuexi.cn/articles/index.html?art_id=15604287883109639930&study_style_id=feeds_default&pid=&ptype=-1&source=share&share_to=wx_single\">湛江市市长姜建军为岭南师院师生上思政课 宣讲《粤港澳大湾区发展规划纲要》</a></p><p style=\"margin-top: 1em; margin-bottom: 1em; text-indent: 2em; text-align: justify; font-size: 10.5pt;\" align=\"\">12月25日，湛江市委副书记、市长姜建军到岭南师范学院为该校师生上思政课，以《抓住大机遇\n \n融入大湾区——学习贯彻&lt;粤港澳大湾区发展规划纲要&gt;》为题开讲。思政课由岭南师范学院党委书记刘明贵主持。开课前，该校校长兰艳泽为姜建军颁发了思政课兼职教授聘书。</p><p style=\"margin-top: 1em;margin-bottom: 1em;text-indent: 2em;text-align: justify;font-size: 14px\" align=\"\">姜建军指出，粤港澳大湾区建设是习近平总书记亲自谋划、亲自部署、亲自推动的重大国家战略。学习贯彻好《粤港澳大湾区发展规划纲要》，是当前和今后一个时期全省、全市的头等大事。姜建军用翔实数据、丰富图表案例对粤港澳大湾区建设的背景意义、机遇挑战、战略定位和发展举措等作了深入浅出的解读，强调湛江要积极抢抓机遇、主动对标对接融入大湾区建设。</p><p style=\"margin-top: 1em;margin-bottom: 1em;text-indent: 2em;text-align: justify;font-size: 14px\" align=\"\">姜建军强调，湛江要抢抓六个方面的机遇融入大湾区建设：抢抓区域协调发展的机遇，打造区域经济发展轴带的重要节点；抢抓创新驱动发展的机遇，打造区域科技创新中心；抢抓基础设施互联互通的机遇，构建现代化基础设施体系；抢抓构建现代产业体系的机遇，积极承接粤港澳大湾区产业转移；抢抓形成全方位开放格局的机遇，扩大高水平对外开放；抢抓建设优质生活圈的机遇，加快建设富有魅力的生态型海湾城市。</p><p style=\"margin-top: 1em;margin-bottom: 1em;text-indent: 2em;text-align: justify;font-size: 14px\" align=\"\">姜建军充分肯定岭南师范学院的办学定位和发展成效，表示要大力支持学校申硕工作和新校区建设，助力学校在融入湛江高质量发展中建成高水平师范大学。他鼓励青年大学生要主动作为，奋发图强，积极投身于湛江对接大湾区建设，为广东推进大湾区建设、实现“四个走在全国前列”、当好“两个重要窗口”作出应有的贡献，谱写青春赞歌。</p><p style=\"margin-top: 1em;margin-bottom: 1em;text-indent: 2em;text-align: justify;font-size: 14px\" align=\"\">刘明贵指出，姜建军市长为师生上了一堂生动的思政课，也是一场精彩的专题报告会。姜市长的报告视野开阔、主题鲜明、内容丰富、针对性强，既有政治高度和思想高度，又有理论深度和实践厚度。我们要深刻认识推进粤港澳大湾区建设的重大意义，准确把握大湾区建设的机遇挑战，积极抢抓机遇，主动对标对接，增强融入大湾区建设的使命担当。</p><p style=\"margin-top: 1em;margin-bottom: 1em;text-indent: 2em;text-align: justify;font-size: 14px\" align=\"\">“在市委和市政府的领导下，湛江未来可期，前景无限光明，对湛江的未来充满信心。”刘明贵指出，湛江正处于多重机遇叠加期，我们要把市委、市政府对学校的关怀和厚爱，转化为办好学校、提升学校发展水平的强大动力，转化为了解湛江、学习湛江、研究湛江、服务湛江的强大能力，以服务湛江为己任，创建湛江首善之区、建设湛江文明高地、打造湛江科教中心，为推动湛江高质量发展、加快建设省域副中心城市、打造现代化沿海经济带重要发展极作出岭南师范学院的更大贡献。</p><p>湛江市政府副市长崔青、市政府秘书长陈才君、市教育局党组书记、局长李更盛；岭南师范学院校领导、党委理论学习中心组成员、机关教辅单位主要负责人以及二级学院党委书记、院长、副书记，马克思主义学院全体教师和学生代表等500多人出席。</p>', 2, '思政课');
INSERT INTO `t_blog` VALUES (9, '学校简介', '　岭南师范学院（原湛江师范学院）是一所具有百年师范教育历史的广东省属普通本科院校，坐落在中国大陆最南端的美丽港城湛江市。学校１９０４年始设师范科教育，１９７８年更名为雷州师范专科学校，１９９１年升格更名为湛江师范学院，２０１４年更名为岭南师范学院。 　　在百余年的师范教育历程中，学校肩负兴学育人的办学使命，凝', '2020-01-13 09:06:28', 3, 0, '<p style=\"line-height: 3em;\"><span style=\"font-family: 仿宋_gb2312,fangsong_gb2312; font-size: 20px;\">　</span><span style=\"font-size: 20px; font-family: 隶书, SimLi;\">岭南师范学院（原湛江师范学院）是一所具有百年师范教育历史的广东省属普通本科院校，坐落在中国大陆最南端的美丽港城湛江市。学校１９０４年始设师范科教育，１９７８年更名为雷州师范专科学校，１９９１年升格更名为湛江师范学院，２０１４年更名为岭南师范学院。&nbsp;</span></p><p style=\"line-height: 3em;\"><span style=\"font-size: 20px; font-family: 隶书, SimLi;\">　　在百余年的师范教育历程中，学校肩负兴学育人的办学使命，凝练了“崇德、博雅、弘志、信勇”的校训精神，坚持“师范性、教学型，地方性、应用型”的办学定位，培养了大批优秀教师、教育工作者和社会应用人才，为粤西地区乃至广东省的教育文化事业和经济社会发展做出应有贡献。学校２０００年成为全国第一所通过教育部本科教学工作合格评估的师范院校，２００７年在教育部本科教学工作水平评估中获得优秀等级，２０１０年被列为广东省新增硕士学位授权立项建设单位，２０１６年列入广东首批普通本科转型试点高校、省市共建高校和广东省大学生创新创业教育示范校，２０１７年被推选为广东省特殊教育教师发展联盟理事长单位，２０１８年列入广东省高等教育“冲一流、补短板、强特色”提升计划建设高校，获批“广东省创建国家教师教育创新实验区”；并先后获得“全国精神文明建设工作先进单位”“全国群众体育工作先进单位”“全国绿化先进集体”“教育部依法治校示范校”“教育部大学英语教学改革示范校”“广东省师德建设先进单位”和“广东省文明校园”等荣誉称号。 <br/>　　学科专业齐全，构建完备的教师教育体系。设有１８个二级学院，建有省优势重点学科２个（教育学、化学）、省特色重点学科４个（课程与教学论、高分子化学与物理、汉语言文字学、材料物理与化学）、“冲补强”重点建设学科３个（教育学、化学和材料科学与工程）。现有本科专业６７个（其中师范类专业３３个），涵盖经济学、法学、教育学、文学、史学、理学、工学、农学、管理学和艺术学等１０大学科门类；拥有国家级特色专业２个（汉语言文学和化学）、省级特色专业６个。师范专业涵盖基础教育、职业教育、学前教育和特殊教育等培养类别，形成了门类齐全、培养培训一体化的完备的教师教育体系。学校面向全国１８个省区招生，全日制在校本科生２３０００多人。<br/>　　优化办学资源配置，不断改善办学条件。校本部占地面积１０００多亩，建筑面积５８万平方米，教学科研仪器设备总值２亿多元。图书馆拥有纸质藏书２３２万册、中外文纸质现期刊１９５５种、电子图书４７０万册、电子期刊５８．８２万多册。建有“全国重点建设职业教育师资培养培训基地”“全国社会工作专业人才培养基地”国家级基地２个及中小学教师、职业教育师资、幼儿园园长、学前教育师资等省级师培基地４个和省级中小学教师发展中心１个，广东高校教师教育教学技能实训中心、艺术实验教学示范中心和化学教学提高型重点实验室等省级教学平台１４个。建有省级决策咨询研究基地１个（中国－东盟教育文化发展研究中心），广东省普通高校哲学社会科学重点实验室１个（特殊儿童心理评估与康复重点实验室），广东省普通高校重点实验室１个（广东省清洁能源材料化学普通高校重点实验室），获批省级工程技术研究中心８个（广东省热带特色资源植物开发工程技术研究中心、广东省粤西资源化工工程技术研究中心、广东省粤西海鲜资源可持续利用工程技术研究中心、广东省辣木资源开发与利用工程技术研究中心、广东省粤西特色生物制品工程技术研究中心、广东省粤西地区太阳能特色利用工程技术研究中心、广东省清洁能源材料化学工程技术研究中心、广东省粤西机电产品设计与制造工程技术研究中心），省级协同创新中心１个（粤台教师教育协同创新发展中心），省高校工程技术开发中心３个（广东高校新材料工程技术开发中心、广东高校热带边缘特色植物资源工程技术开发中心、广东高校数字化学习工程技术开发中心），广东省高校重点提升平台１个（广东高校新型光电功能材料研究开发中心），广东高校人文社科重点研究基地１个（粤西教师教育研究中心），广东省现代农业科技创新中心１个（广东省热带特色资源植物科技创新中心）、广东高校国际联合实验室１个（岭南师范学院与香港城市大学先进薄膜材料联合实验室），广东省实践科学发展观研究基地１个（城乡协调发展研究基地），湛江市重点实验室４个。设有教育研究院、新材料研究院、岭南文化研究院等校级研究机构３个及研究所６１个。<br/>　　以优师为本，建设高水平师资队伍。现有教职员工１７００余人，其中“珠江学者”１名，高级职称教师５５０多名，硕士学位以上教师近１３００名；１５１人入选广东省“千百十工程”培养对象，６０多人被聘为兄弟院校硕士和博士生导师，６２人获授国务院政府特殊津贴、全国“五一”劳动奖章、全国优秀教师、省级教学名师和南粤优秀教师等称号；４８人被遴选为广东省“扬帆计划”人才；９人被遴选为广东省高等学校优秀青年教师培养对象；３０人被评为省级“强师工程”资助对象。聘有一批国内外知名专家学者为客座教授和兼职教授。<br/>　　创新人才培养模式，培养高素质应用型人才。树立德智体美劳全面发展的“负责任、强能力、善创新”的高素质应用型人才培养目标。着重推行“教学研做”互动合一，培养具有驾驭课堂的教学力、体验反思的教研力、协同创新的实践力、心志专一的坚持力和为人师表的引导力的“五力型”优质教师。近年来，学生在全国及省级学术科技、专业实验、师范生技能、体育竞技、外语口语、艺术表演和社会实践等竞赛活动中屡获殊荣。毕业生考研录取人数逐年增长，２０１８年考取６００人，考研录取率突破１０%，就业率居省内高校前列，毕业生扎根粤西，挺进珠三角，深入京、津、沪、宁、藏等地就业，被国内主流媒体誉为“粤西过江龙”和“援藏良驹”。 <br/>　　协同创新和育人，加强国际交流与校地合作。注重开展教育国际交流与合作。１９９８年开始招收外国留学生，已培养培训长短期留学生６００多人。同美国、英国、德国、澳大利亚、俄罗斯、日本、新西兰、白俄罗斯、乌克兰、印尼、泰国、韩国、越南和港澳台地区的５０多所境外高校和教育机构以及近３０所中小学建立了紧密的合作关系，共同开展学术交流、专业合作、教育培训、教师互聘和学生跨文化学习交流活动，每年３００多名学生出国学习、实习和研习。与亚洲潜水联合会合作举办“亚洲潜水学院”；与全国工商联水产业商会等合作举办“水产国际商务学院”；与白俄罗斯国立体育大学合作举办以中国武术和体育康复保健为特色的孔子课堂，并被评为全球“先进孔子课堂”；与白俄罗斯国家科学院哲学研究院联合成立“中白哲学－文化研究中心”；与广东东岛新能源股份有限公司联合成立“东岛清洁能源材料产业学院”。学校被命名为“广东省中华文化传承基地”。充分发挥在人才培养、科技研究、智力支持等方面的优势，与地方政府部门、企业、科研机构、驻地部队等各类单位紧密联系，在教育、科技、经济、文化、法律与政策研究等方面开展全方位合作，在积极拓展办学资源的同时，不断提升学校对地方经济社会发展的贡献率。<br/>　　当前，在“双一流”建设背景下，学校深入贯彻全国教育大会精神，坚持“错位竞争、内涵发展、特色兴校”的发展策略，立足教师教育改革创新，立足服务区域经济社会发展，以提高办学质量为核心，以争取硕士学位授予权为动力，大力推进“新师范”“新工科”建设，实施“强师工程”“质量提升工程”“体制机制改革工程”“资源配置优化工程”“民心凝聚工程”“党的建设工程”等六大工程，不断提高办学水平和教育质量，努力建设成为一所特色鲜明的高水平师范大学。</span></p><p style=\"line-height: 3em;\"><span style=\"font-size: 20px; font-family: 隶书, SimLi;\">　　学校现任党委书记刘明贵、校长兰艳泽。</span></p><p style=\"line-height: 3em;\"><span style=\"font-family: 仿宋_gb2312,fangsong_gb2312; font-size: 18px;\">　　　　　　　　　　　　　　　　　　　　　　　　　　　更新日期：2018年12月25日</span></p><p><br/></p>', 5, '学校简介');
INSERT INTO `t_blog` VALUES (10, '学校标识', '新校徽，以钟为主图案，有时间、积聚与警示的意义。表明我校始于1636年的雷阳书院而源远流长，同时取其“钟铎声声，人文日新；昌明博大，蒸蒸日上”之意，预示着我校恢宏壮阔的发展前景；以正圆围绕方钟，表示具有“自强不息、发展不已”精神的岭师人追求完美与和谐。钟取“人”形，喻示教育以人为本。圆周上部的校训，说明了学校', '2020-01-13 09:07:23', 6, 1, '<p style=\"text-indent: 37px\"><span style=\"font-family: 宋体;font-size: 19px\">新校徽，以钟为主图案，有时间、积聚与警示的意义。表明我校始于</span><span style=\"font-size: 19px\">1636</span><span style=\"font-family: 宋体;font-size: 19px\">年的雷阳书院而源远流长，同时取其“钟铎声声，人文日新；昌明博大，蒸蒸日上”之意，预示着我校恢宏壮阔的发展前景；以正圆围绕方钟，表示具有“自强不息、发展不已”精神的岭师人追求完美与和谐。钟取“人”形，喻示教育以人为本。圆周上部的校训，说明了学校的人才培养规格与要求。圆周两侧的三层枝叶，表示我校枝繁叶茂，蓬勃发展。圆周下部的中英文校名以蓝色为背景，既表示我校地处南海之滨，又喻示了学校“立足湛江，面向全省，走向全国，胸怀世界”的发展定位。</span></p><p style=\"text-indent: 37px\"><span style=\"font-family: 宋体;font-size: 19px\"><br/></span></p><p style=\"text-align: center\"><span style=\"color: rgb(51, 51, 51);font-family: 黑体;font-size: 21px\">崇德　 博雅　　弘志　　信勇</span></p><p style=\"text-align: center\">&nbsp;</p><p style=\"line-height: 31px\"><span style=\"font-family: 宋体;font-size: 19px\">　　校训是学校规定的对学生有指导意义的语辞，它是一个学校人才培养规格简明而又极具概括性的表述。校训内容的确定既与学校的性质、地位有关，更与校长的办学思想关系密切，它常常是校长办学理念的集中体现。人们从校训中，可以清楚地了解到校长的办学思想和对人才培养的规格与要求，也能在一定程度上把握学校所追求的办学特色。因而许多学校的校长都非常重视校训。</span> &nbsp; &nbsp; &nbsp;</p><p style=\"line-height: 31px;text-indent: 38px\"><span style=\"font-family: 宋体;font-size: 19px\">校领导在对我校的办学现状进行深入调查研究的基础上，基于形成我校鲜明的办学特色，提升我校的办学地位，乃至成为师范大学的远大理想，提出</span><span style=\"font-family: 宋体;font-size: 19px\">“崇德、博雅、弘志、信勇”八个字作为校训，从而既把办学理念转变成了具体的可操作的评价标准，又使广大学生明确了做人与做学问的要求和规范。</span> &nbsp; &nbsp; &nbsp;</p><p style=\"line-height: 31px\"><span style=\"font-family: 宋体;font-size: 19px\">　　师范院校是教师的摇篮，教师最重要的是为人师表，为人师表的核心是教师能以高尚的品德成为学生的楷模。因而校领导在校训中把“崇德”放在首位。对于品德的崇尚是中华民族的优良传统，早在《尚书·武成》篇中就有“崇德报功”的提法，即尊崇有德和报答有功的人。其后孔子既以德行等四科教授弟子，又以忠诚信实、仰慕道义的具体解说来回答子张怎样才能“崇德”的疑惑。崇德，就是崇尚、重视道德，进而提高自己的道德品质。德，从层次来说，有基本道德和高尚道德之分。从范畴来说，有公共道德和职业道德之分。作为一个接受高等教育的大学生来说，既要具备基本的道德素质，也要朝着高尚的道德努力进取。既要遵守公共道德，也要有高尚的职业道德。对于师范院校的学生来说，职业道德就是师德。师德的基本内容包括爱岗敬业，乐于奉献，对学生严而不暴，爱而不流，举止大方而文雅。教师德行的高低，是其能否成为一名合格教师的首要前提。古人云：“教人治人，宜皆以正直为先”，说的就是这个意思。而“经师易求，人师难得”，更是从反面说明品德高尚是一个教师最为重要的素质。</span></p><p style=\"line-height: 31px\"><span style=\"font-family: 宋体;font-size: 19px\">　　“博雅”，指的是既具有渊博的知识，又有着高雅的志趣，温文尔雅的风度。教师作为知识的传播者，必须首先具备的基本业务素质就是知识的渊深和广博，传授知识时旁征博引，左右逢源；解答疑难时如庖丁解牛，游刃有余，这样才能赢得学生的尊重和敬佩。相反，只能照本宣科，离开书本即了无余言，或者“以其昏昏，使人昭昭”，甚或经常搜索枯肠也不能解答学生的疑难，显然有亏教师的称号。俗话说，给学生一碗水，教师自己首先必须要有一桶水。这是古今中外教育实践的经验之谈。值得指出的是，教师不仅以知识的富有而为人师，还应以志趣的高雅和风度的温文来成为学生的楷模。教书育人是人类社会中一种极其严肃高尚的行为，作为行为主体的教师，其言行举止都应文明雅致，有如玉树临风、松菊有神，使学生在与教师的交往中潜移默化地受到感染，以至移情易性，变化气质。历史上许多教育名人以其博学赢得弟子的敬仰，但更以其高雅的情志和神韵征服了学生的心灵，甚或影响他们的一生。</span></p><p style=\"line-height: 31px\"><span style=\"font-family: 宋体;font-size: 19px\">　　“弘志”，就是要恢宏大志，理想高远。人无大志，有如蓬间小雀，快意于嬉戏榆枋之间，终不能翱翔云天。所谓“崇峻不凌霄，则无弥天之云”，说的就是这个道理。相反，“苟怀四方志，所在可游盘”，完全能创造出轰轰烈烈的人生。所以先哲们都非常重视志向的确立，孔子教导学生：“士不可不弘毅，任重而道远。”王阳明给学生的训示是：“志不立，天下无可成之事。”吕坤告诫后生要有这样的志向：“做第一等人，干第一等事，说第一等话，抱第一等识。”</span><span style=\"font-family: 宋体;font-size: 19px\">从这个意义上说，无论是才高八斗的智者，还是智能平平的凡人；无论是势处春风得意的顺境，还是身在坎坷不平的困境，都必须志存高远。勿庸讳言，我们学校地处祖国边陲粤西一隅，远离政治经济文化中心，且为新办本科师范院校，办学条件与名牌大学差距较大，但正因为如此，就越发需要我们弘扬大志，恢廓胸襟，而不要妄自菲薄，自我气短。</span> &nbsp; &nbsp; &nbsp;</p><p style=\"line-height: 31px\"><span style=\"font-family: 宋体;font-size: 19px\">　　“信勇”，指的是既有自信而又能诚信待人，且能勇于探索，敢为人先。－个人禀赋有高低，能力有大小，但自信之心不可少。人无自信，犹草木之无神。故古今名人大都以自信自勉，文豪李白坚信：“天生我才必有用”，伟人毛泽东年轻时就宣称：“自信人生二百年，会当水击三千里。”正因为充满自信，才有“雄关漫道其如铁，而今迈步从头越”，“数风流人物，还看今朝”的英雄气概。我校虽然属于一般本科师范院校，但我们相信自己有与名校学生“试比高”的勇气和才华。自信固然不可少，但作为未来的人民教师的大学生，忠诚信实，更应是我们必须具备的品德。追求人际关系的和谐，是我们始终如一的目标，而人际关系和谐的重要基础是人与人之间信实无欺。故管子提出：“诚信者，天下之结也。”冯梦龙以为：“信，国之宝也，民之所凭也。”孔子感叹道：“人而无信，不知其可乎！”教师既是为人师表，自然应当讲信修睦，而不能信口雌黄，奸诈无诚。具备了信的品德，还需要有勇的作风。探索自然界的奥秘，需要有不怕失败的勇敢精神；寻求治国平天下的真理，需要有敢于献身的勇气；改变自己的命运，需要有勇于面对耻辱的气魄，所以孔子说：“知耻近乎勇”。年轻人富于朝气，多于锐气，再能济之以勇气，就会奋斗不息，探索不止。</span> &nbsp; &nbsp; &nbsp;</p><p style=\"line-height: 31px;text-indent: 9px\"><span style=\"font-family: 宋体;font-size: 19px\">　“崇德”“弘志”，要求学生重视品德修炼，恢宏志气，临大节而不苟，搴帅旗而志有余，讲的是做人的品格志气问题。“博雅”“信勇”，要求学生追求知广情雅，信而有勇；学富五车而风流俊赏，信诚毕具而又勇为人先，讲的是成才所需要的学识和创新精神等素质问题。前后相应，而又有着内在的紧密联系，德才兼重，内美外修，循此以行，俊才名师，可期而见。</span></p><p style=\"text-align: right; line-height: 31px;\"><span style=\"font-family: 宋体;font-size: 19px\">（原岭南师范学院副院长刘周堂教授）</span></p>', 5, '学校标识');
INSERT INTO `t_blog` VALUES (11, '最代码官方验证通过', 'zuidaima.com验证通过', '2020-01-22 11:23:37', 5, 1, '<p>zuidaima.com验证通过<br/></p>', 1, '最代码');

-- ----------------------------
-- Table structure for t_blogger
-- ----------------------------
DROP TABLE IF EXISTS `t_blogger`;
CREATE TABLE `t_blogger`  (
  `id` int(11) NOT NULL AUTO_INCREMENT,
  `name` varchar(50) CHARACTER SET utf8 COLLATE utf8_general_ci NULL DEFAULT NULL,
  `password` varchar(100) CHARACTER SET utf8 COLLATE utf8_general_ci NULL DEFAULT NULL,
  `profile` text CHARACTER SET utf8 COLLATE utf8_general_ci NULL,
  `nickName` varchar(50) CHARACTER SET utf8 COLLATE utf8_general_ci NULL DEFAULT NULL,
  `sign` varchar(100) CHARACTER SET utf8 COLLATE utf8_general_ci NULL DEFAULT NULL,
  `imageName` varchar(100) CHARACTER SET utf8 COLLATE utf8_general_ci NULL DEFAULT NULL,
  `email` varchar(45) CHARACTER SET utf8 COLLATE utf8_general_ci NULL DEFAULT NULL,
  `phone` varchar(11) CHARACTER SET utf8 COLLATE utf8_general_ci NULL DEFAULT NULL,
  PRIMARY KEY (`id`) USING BTREE
) ENGINE = InnoDB AUTO_INCREMENT = 2 CHARACTER SET = utf8 COLLATE = utf8_general_ci ROW_FORMAT = Dynamic;

-- ----------------------------
-- Records of t_blogger
-- ----------------------------
INSERT INTO `t_blogger` VALUES (1, 'admin', 'admin', '<p><span style=\"color: rgb(102, 102, 102); font-family: &quot;microsoft yahei&quot;; font-size: 12.8px; background-color: rgb(255, 255, 255);\"><img src=\"http://img.baidu.com/hi/jx2/j_0002.gif\"/><img src=\"http://img.baidu.com/hi/jx2/j_0002.gif\"/><img src=\"http://img.baidu.com/hi/jx2/j_0002.gif\"/></span></p><p><span style=\"color: rgb(102, 102, 102); font-family: &quot;microsoft yahei&quot;; font-size: 12.8px; background-color: rgb(255, 255, 255);\">曾虑多情损梵行，入山又恐别倾城。世上安得两全法，不负如来不负卿。爱终究是来日方长的秘密...&nbsp;</span></p><p><img src=\"http://img.baidu.com/hi/jx2/j_0058.gif\"/><img src=\"http://img.baidu.com/hi/jx2/j_0058.gif\"/><img src=\"http://img.baidu.com/hi/jx2/j_0058.gif\"/></p><p><span style=\"color: rgb(102, 102, 102); font-family: &quot;microsoft yahei&quot;; font-size: 12.8px; background-color: rgb(255, 255, 255);\">从前初识这世间, 万般流连, 看着天边似在眼前, 也甘愿赴汤蹈火去走它一遍, 如今走过这世间, 万般流连, 翻过岁月不同侧脸, 措不及防闯入你的笑颜, 我曾难自拔于世界之大， 也沉溺于其中梦话， 不得真假 不做挣扎 不惧笑话， 我曾将青春翻涌成你， 也曾指尖弹出盛夏， 以爱之名 你还愿意吗？</span></p>', '最代码博客', '爱终究是来日方长的秘密！', '20200113083822.jpg', 'zuidaima@qq.com', '18676221615');

